import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, FlatList, Image } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const mockData = [
  {
    id: '1',
    title: 'Pedido #1234',
    description: 'Entrega de comida para restaurante XYZ',
    image: 'data:image/jpeg;base64,',
    distance: '5 km',
    deliveryTime: '30 min'
  },
  {
    id: '2',
    title: 'Pedido #5678',
    description: 'Entrega de pacote para cliente ABC',
    image: 'data:image/jpeg;base64,',
    distance: '2 km',
    deliveryTime: '15 min'
  },
  // Adicione mais itens conforme necessário
];

export default function AvailableOrders () {
  const [search, setSearch] = useState('');

  const filteredData = mockData.filter((item) =>
    item.title.toLowerCase().includes(search.toLowerCase())
  );

  const renderItem = ({ item }: any) => (
    <View className="bg-white p-4 mb-4 rounded-lg shadow-lg flex-row items-center">
      <View className='flex bg-white'>
        <Image source={{ uri: item.image }} className="w-full h-20 rounded-lg mb-1" />
        <TouchableOpacity className="bg-green-500 p-2 rounded-lg flex-row items-center justify-evenly">
          <Text className="text-white text-sm font-semibold">Aceitar</Text>
          <Ionicons name="checkmark-circle" size={20} color="white" className="m-auto" />
        </TouchableOpacity>
      </View>
      <View className="flex-1 ml-3">
        <Text className="text-xl font-semibold text-gray-800">{item.title}</Text>
        <Text className="text-lg text-gray-600 mt-1">{item.description}</Text>
        <View className="flex-row justify-between mt-2">
          <Text className="text-base text-gray-500">Distância: {item.distance}</Text>
          <Text className="text-base text-gray-500">Tempo: {item.deliveryTime}</Text>
        </View>
      </View>
    </View>
  );

  return (
    <View className="flex-1  p-4">
      {/* Barra de Busca */}
      <View className=" p-4 rounded-lg shadow-lg mb-4">
        <TextInput
          className="w-full p-3 border border-gray-300 rounded-lg text-base"
          placeholder="Buscar pedidos..."
          value={search}
          onChangeText={(text) => setSearch(text)}
        />
      </View>

      {/* Lista de Pedidos */}
      <FlatList
        className='bg-white-100'
        data={filteredData}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
        contentContainerStyle={{ paddingBottom: 16 }} // Optional: Add padding to the bottom
      />
    </View>
  );
}
